// Imports
import React, {useState} from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./css/registration.css";
import GeneralButton from "../general/GeneralButton";



// Registration component
const Registration = () => {
    const navigate = useNavigate();


    // Function to loop over inputs and get the values
    function fcnGetValues() {
        let data = {};

        document.querySelectorAll(".registerInput").forEach(function(item) {
            data[item.name] = item.value;
        });

        return data;
    }


    // Handle registration
    function handleRegistration() {
        const data = fcnGetValues();

        axios
            .post("http://localhost:8000/api/auth/register/", data)
            .then((res) => {
                localStorage.setItem("auth", JSON.stringify({
                    access: res.data.access,
                    refresh: res.data.refresh,
                    user: res.data.user
                }));

                navigate("/");
            })
            .catch((err) => {
                console.log("Error in the registration: ", err);
            })

    }


    return (
        <div>
            <div>
                <h3>Registration form</h3>
            </div>

            <div>
                <label htmlFor="inputRegistrationEmail">Email</label>
                <input 
                    maxLength={50} 
                    type="email" 
                    id="inputRegistrationEmail" 
                    placeholder="Email" 
                    className="registerInput"
                    name="email"
                    required
                />

                <label htmlFor="inputRegistrationName">First name</label>
                <input                     
                    maxLength={50} 
                    type="text" 
                    id="inputRegistrationName" 
                    placeholder="First name"
                    className="registerInput"
                    name="first_name"
                />

                <label htmlFor="inputRegistrationSurname">Last name</label>
                <input 
                    maxLength={50} 
                    type="text" 
                    id="inputRegistrationSurname" 
                    placeholder="Last name"
                    className="registerInput"
                    name="last_name"
                />

                <label htmlFor="inputRegistrationPassword">Password</label>
                <input 
                    maxLength={20} 
                    type="password" 
                    id="inputRegistrationPassword" 
                    placeholder="Password" 
                    className="registerInput"
                    name="password"
                    required
                />                
            </div>            

            <div>
                <GeneralButton
                    children={'Register'}
                    onClick={handleRegistration}
                />
            </div>            
        </div>        
    )
};



// Export
export default Registration;
