// Imports
import React, { useEffect, useState } from "react";
import "./css/evaluationPage.css";
import NavigationBar from "../navBar/NavBar";
import EvaluationList from "./EvaluationList";
import { useNavigate } from "react-router-dom";
import GeneralButton from "../general/GeneralButton";
import axiosService from "../../helpers/axios";



// Evaluation component
const EvaluationPage = () => {
    const navigate = useNavigate();
    const [companyList, setCompanyList] = useState();

    // List of companies to create the evaluation
    useEffect(() => {
        axiosService
            .get(`/company`)
            .then(res => res.data)
            .then((data) => {
                setCompanyList(() => [...data]);
            })
    }, []);


    // Create evaluation component
    const CreateEvaluation = () => {
        return (
            <div id="divCreateEvaluation">
                <div>
                    <h3 htmlFor="">Choose a company to evaluate</h3>
                </div>

                <div>
                    {companyList.length === 0 
                        ?
                            <label id="lblCompaniesListCard">The list is empty</label> 
                        :

                            companyList?.map((element, index) => (
                                <label key={index}>
                                    {element.name}
                                    <input type="radio" name="companyRadio" id={element.id} />                                    
                                </label>
                            ))
                    }
                </div>
            </div>
        )
    }



    // Return evaluation page
    return (
        <div id="divEvaluationPage">
            <div id="divEvaluationFirstBar">
                <h1>Evaluations Menu</h1>

                <GeneralButton
                    children='Create'
                    onClick={() => navigate("/create-evaluation", {state:{mode:'Create'}})}
                />
            </div>

            <NavigationBar/>
            <EvaluationList/>

            <p className="evaSeparatorParagraph"/>

            <CreateEvaluation/>

            <p className="evaSeparatorParagraph"/>
        </div>
    )
};



// Export
export default EvaluationPage;
