// Imports
import React, {useState, useEffect} from "react";
import { useNavigate } from "react-router-dom";
import axiosService from "../../helpers/axios";
import "./css/companiesList.css";
import DeleteCompany from "./CompanyDelete";



// Companies list
const CompaniesList = () => {
    /* States */
    const [companiesList, setCompaniesList] = useState([]);    
    const [showViewCompany, setShowViewCompany] = useState('none');
    const [companyObject, setCompanyObject] = useState();


    /* List of companies */
    useEffect(() => {
        axiosService
            .get(`/company`)
            .then(res => res.data)
            .then((data) => {
                setCompaniesList(() => [...data]);
            })
    }, []);


    const CompanyCard = ({name, id}) => {      
        const navigate = useNavigate();


        /* Handle the view modal */
        function handleClickView() {
            setShowViewCompany('block')

            axiosService
                .get(`/company/${id}`)
                .then(res => res.data)
                .then((data) => {
                    setCompanyObject(data);
                })
        }


        /* Edit the company */
        function handleClickEdit() {         
            navigate("/create-company", {state:{mode:'Edit', id:id}});
        }



        // Return company card
        return (
            <div id="companyCard">
                <div id="divCompanyCard">
                    <div id="divCompanyCardLabels">
                        <label className="label">Company<br/>name</label>
                        <textarea disabled className="name" value={name}></textarea>                            
                    </div>

                    <div id="divCompanyCardButtons">
                        <button onClick={handleClickView}>View</button>
                        <button onClick={handleClickEdit}>Edit</button>
                        <button>Delete</button>
                    </div>                
                </div>
            </div>
        )
    }



    // Return companies list
    return (
        <>
            <DeleteCompany
                id={id}
            />

            <div id="companiesListTitle">
                <label htmlFor="">Companies list</label>
            </div>

            <div id="companiesListCard">
                {companiesList.length === 0 
                    ?
                        <label id="lblCompaniesListCard">The list is empty</label> 
                    :

                        companiesList?.map((element, index) => (
                            <CompanyCard 
                                key={index} 
                                name={element.name}                                
                                id={element.id}                                
                            />
                        ))
                }
            </div>
        </>
    )
};



// Export
export default CompaniesList;
