// Imports
import React, {useEffect, useState} from "react";
import "./css/evaluationList.css";
import { useNavigate } from "react-router-dom";
import axiosService from "../../helpers/axios";



// Evaluation list component
const EvaluationList = () => {
    /* States */
    const [evaluationList, setEvaluationList] = useState([]);        
    const [evaluationObject, setEvaluationObject] = useState({});
    const [showDelete, setShowDelete] = useState({visibility:'hidden', opacity:'0'});
    const [showMessage, setShowMessage] = useState({height:'0', fontSize:'0rem'});


    /* List of evaluations */
    useEffect(() => {
        axiosService
            .get(`/evaluation`)
            .then(res => res.data)
            .then((data) => {
                setEvaluationList(() => [...data]);
            })
    }, []);


    // Evaluation card to loop over with the evaluations already created
    const EvaluationCard = ({name, id}) => {
        const navigate = useNavigate();


        /* Edit the evaluation */
        function handleClickEdit() {         
            navigate("/create-evaluation", {state:{mode:'Edit', id:id}});
        }


        // Delete the evaluation
        function handleDelete(id, name) {            
            setEvaluationObject({'id':id, 'name':name});
            setShowDelete({visibility:'visible', opacity:'1'})
        }



        // Return evaluation card
        return (
            <div id="EvaluationCard">
                <div id="divEvaluationCard">
                    <div id="divEvaluationCardLabels">
                        <label className="label">Evaluation<br/>name</label>
                        <textarea disabled className="name" value={name}></textarea>
                    </div>

                    <div id="divEvaluationCardButtons">
                        <button onClick={handleClickEdit}>Edit</button>
                        <button onClick={() => handleDelete(id, name)}>Delete</button>
                    </div>                
                </div>
            </div>
        )
    }



    // Return Evaluation card
    return (
        <div id="divEvaluationList">


        </div>
    )
}



// Export
export default EvaluationList;
