// Imports
import React from "react";
import { getUser } from "../hooks/actions";
import { useUserActions } from "../hooks/actions";
import "./css/navBar.css";
import { useNavigate } from "react-router-dom";
import GeneralButton from "../general/GeneralButton";



// Navigation bar
const NavigationBar = () => {

    const GeneralBar = () => {            
        return (
            <div id="background_menu">
                <div id="bar_menu">
                    <h3>SME-AS</h3>

                    <div id="user_identification">
                        { getUser()
                            ?
                                <>
                                    <h6 style={{color:'grey'}}>User:<span>{getUser().email}</span></h6>                                
                                </>
                            :
                                <>
                                    <h6 style={{color:'gray'}}>User</h6>
                                    <h4>Anonymous</h4>
                                </>
                        }
                    </div>                
                </div>
            </div>
        )        
    }


    const ButtonsBar = () => {
        /* Constants */    
        const userActions = useUserActions();
        const navigate = useNavigate();


        // To handle buttons click
        function handleClick(e) {            
            document.querySelectorAll('.navBarButtons').forEach(function(item) {                
                if (item.getAttribute('data-page') === e.target.getAttribute('data-page')) {
                    item.setAttribute('style', 'color:blue')
                } else {
                    item.setAttribute('color', 'black')
                }
            });

            navigate(e.target.getAttribute('data-page'));

        }


        return (
            <>
                <div id="div-main">
                    <div className="navbar" style={{padding:0}}>

                        <GeneralButton
                            //onClick={() => navigate('/company')}
                            onClick={(e) => handleClick(e)}
                            children="Companies"
                            value="Companies"
                            dataPage='/company'
                            className='navBarButtons'
                        />

                        <GeneralButton
                            children="Evaluations"
                            onClick={(e) => handleClick(e)}  
                            value="Evaluations"
                            dataPage='/evaluations'
                            className='navBarButtons'
                        />


                        <GeneralButton
                            onClick={() => userActions.logout()}                         
                            children="Logout"
                            value='Logout'                            
                        />
                    </div>
                </div>        
            </>
        )
    }



    return (
        <>
            <GeneralBar/>
            <ButtonsBar/>            
        </>    
    );
}



// Export
export default NavigationBar;
