// Imports
import React from "react";
import { getUser } from "../hooks/actions";
import "./css/navBar.css";



// Navigation bar
const NavigationBar = () => {

    const GeneralBar = () => {            
        return (
            <div id="background_menu">
                <div id="bar_menu">
                    <h3>SME-AS</h3>

                    <div id="user_identification">
                        { getUser()
                            ?
                                <>
                                    <h6 style={{color:'grey'}}>User:<span>{getUser().email}</span></h6>                                
                                </>
                            :
                                <>
                                    <h6 style={{color:'gray'}}>User</h6>
                                    <h4>Anonymous</h4>
                                </>
                        }
                    </div>                
                </div>
            </div>
        )        
    }


    const ButtonsBar = () => {
        /* Constants */    
        const userActions = useUserActions();     
        const user = getUser()    
        const style = {height:'2rem', backgroundColor: 'var(--brown_100)', 
                        fontSize: '1.0rem', borderWidth: '0', textAlign:'center'};
        const navigate = useNavigate();
        const location = useLocation();


        /* Use Effect */
        useEffect(() => {        
            onClickBtnMainMenu(location.pathname);
        })



        /* Return */
        return (
            <>
                <div id="div-main" style={{top:'4rem'}}>
                    <div className="navbar" style={{padding:0}}>
                        <ButtonCss
                            onClick={() => navigate('/evaluations-page')}
                            children="Evaluations"
                            style={style}
                            dataPage="/evaluations-page"
                            value="Evaluations"
                        />

                        <ButtonCss
                            children="Companies"
                            onClick={() => navigate('/companies-page')}
                            dataPage="/companies-page"
                            style={style}
                            value="Companies"
                            className='btnMainMenu'
                        />

                        {parseInt(user.user_type) < 3 
                            ?   
                                <>
                                    <ButtonCss
                                        onClick={() => navigate('/relation-page')}
                                        children="Relations"
                                        style={style}                                    
                                        value="Relations"
                                        dataPage="/relation-page"
                                        className='btnMainMenu'
                                    />                         

                                    <ButtonCss
                                        onClick={() => navigate('/element')}
                                        children="Elements"
                                        style={style}
                                        value="Elements"
                                        dataPage='/element'
                                        className='btnMainMenu'
                                    />
                                </>                            
                            : null
                        }

                        {parseInt(user.user_type) === 1
                            ?
                                <>
                                    <ButtonCss                                    
                                        children="Users"
                                        style={style}
                                        value="Users"
                                        className='btnMainMenu'
                                    />

                                    <ButtonCss                                     
                                        children="Administration"                                    
                                        style={style}
                                        value="Administration"
                                        className='btnMainMenu'
                                    />
                                </>
                            : null
                        }

                        <ButtonCss 
                            onClick={() => userActions.logout()}                         
                            children="Logout"
                            style={style}                        
                        />

                        <ButtonCss                        
                            children="Reset password"
                            style={style}
                            value="Reset password"
                        />            
                    </div>
                </div>        
            </>
        )
    }



    return (
        <>
            <GeneralBar/>
            <ButtonsBar/>
        </>    
    );
}



// Export
export default NavigationBar;
