// Imports
import React, { useRef, useState } from "react";
import GeneralButton from "../general/GeneralButton";
import { useLocation, useNavigate } from "react-router-dom";
import "./css/evaluationEditCreate.css";
import axiosService from "../../helpers/axios";
import { getUser } from "../hooks/actions";



// Create edit component
const CreateEditEvaluation = () => {
    const navigate = useNavigate();
    const [evaluationName, setEvaluationName] = useState();
    const initialName = useRef();    
    const user = getUser();


    // Location brings the edit mode if applicable
    const location = useLocation();


    // Get the company to be edited if mode is Edit
    if (location.state.mode === "Edit") {
        axiosService
            .get(`/evaluation/${location.state.id}/`)
            .then(res => res.data)
            .then((data) => {
                setEvaluationName(() => data.name);
            })
    }


    // Handle create or edit click
    function handleCreateEdit() {
        let data = {};

        if (initialName.current.length < 3) {
            return
        } else {
            data = {
                'name':initialName.current,
                'user':user.id
            }
        }


        if (location.state.mode === "Create") {
            axiosService                
                .post("/evaluation/", data)
                .then((res) => {
                    navigate(-1)
                })            
        } else {
            axiosService
                .put(`/evaluation/${location.state.id}/`, data)
                .then(() => {
                    navigate(-1)
                })            
        }
    }


    // To handle each time a key is pressed in the textarea
    function handleOnChange(e) {
        initialName.current = e.target.value;        
    }



    // Return
    return (
        <>
            <div id="divEvaluationCreateButtons">
                <GeneralButton
                    children={location.state.mode}
                    onClick={handleCreateEdit}
                />

                <GeneralButton
                    children='Previous'
                    onClick={() => navigate(-1)}
                />
            </div>

            <div id="divEvaluationData">
                <h1>Evaluation data</h1>

                <label htmlFor="evaluationName">Name</label>
                <textarea 
                    name="evaluationName" 
                    id="" 
                    placeholder="Evaluation name" 
                    defaultValue={evaluationName}
                    onChange={(e) => handleOnChange(e)}
                    maxLength={50}
                ></textarea>
            </div>
        </>

    )
};



// Export
export default CreateEditEvaluation;
