// Imports
import React from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { fcnGetValues } from "./Various";
import GeneralButton from "../general/GeneralButton";
import "./css/login.css";



// Login component
const Login = () => {
    const navigate = useNavigate();

    // Handle login
    function handleLogin() {
        const data = fcnGetValues('loginInput');

        axios
            .post("http://localhost:8000/api/auth/login/", data)
            .then((res) => {
                localStorage.setItem("auth", JSON.stringify({
                    access: res.data.access,
                    refresh: res.data.refresh,
                    user: res.data.user
                }));

                navigate("/");
            })
            .catch((err) => {
                console.log("Error in the login: ", err);
                navigate("/login");
            })
    };


    // Return
    return (
        <div id="divLogin">
            <div style={{marginTop:'1rem'}}>
                <h1>Login form</h1>
            </div>

            <div id="divLoginHidden">
                <h2>Email or password incorrect</h2>
            </div>

            <div id="divLoginInput">
                <label htmlFor="inputLoginEmail">Email</label>
                <input 
                    maxLength={50} 
                    type="email" 
                    id="inputLoginEmail" 
                    placeholder="Email" 
                    className="loginInput"
                    name="email"
                    required
                />

                <label htmlFor="inputLoginPassword">Password</label>
                <input 
                    maxLength={20} 
                    type="password" 
                    id="inputLoginPassword" 
                    placeholder="Password" 
                    className="loginInput"
                    name="password"
                    required
                />                
            </div>            

            <div>
                <GeneralButton
                    children={'Login'}
                    onClick={handleLogin}
                />
            </div>            
        </div>
    )
};



// Export
export default Login;
