// imports
import React from "react";
import axiosService from "../../helpers/axios";
import "./css/comopanyDelete.css";
import GeneralButton from "../general/GeneralButton";



// Delete component
const DeleteCompany = ({id, name, setCompanyDeleted, setShowDelete}) => {

    function handleDelete() {
        axiosService
            .delete(`/company/${id}/`)
            .then(res => {                
                setCompanyDeleted(true);
            })
            .catch(e => {
                console.log("Error deleting the evaluation ", e);
            })
    }


    return (
        <div id="divDeleteCompany">
            <div id="divDeleteCompanyMain">
                <label htmlFor="">Are you sure you want to delete the company {name}?</label>

                <div>
                    <GeneralButton
                        children='Cancel'
                        onClick={() => setShowDelete({visibility:'hidden', opacity:'0'})}
                    />

                    <GeneralButton
                        children='Delete'
                        onClick={handleDelete}
                    />
                </div>
            </div>
        </div>        
    )
};



// Export
export default DeleteCompany;
