// Imports
import React from "react";
import "./css/evaluationList.css";
import { useNavigate } from "react-router-dom";



// Evaluation list component
const EvaluationList = ({setEvaluationObject, setShowDelete}) => {

    // Company card to loop over with the companies already created
    const EvaluationCard = ({name, id}) => {
        const navigate = useNavigate();


        /* Edit the company */
        function handleClickEdit() {         
            navigate("/create-evaluation", {state:{mode:'Edit', id:id}});
        }


        // Delete the company
        function handleDelete(id, name) {            
            setEvaluationObject({'id':id, 'name':name});
            setShowDelete({visibility:'visible', opacity:'1'})
        }



        // Return company card
        return (
            <div id="EvaluationCard">
                <div id="divEvaluationCard">
                    <div id="divEvaluationCardLabels">
                        <label className="label">Evaluation<br/>name</label>
                        <textarea disabled className="name" value={name}></textarea>
                    </div>

                    <div id="divEvaluationCardButtons">
                        <button onClick={handleClickEdit}>Edit</button>
                        <button onClick={() => handleDelete(id, name)}>Delete</button>
                    </div>                
                </div>
            </div>
        )
    }



    // Return Evaluation card
    return (
        <div id="divEvaluationList">


        </div>
    )
}



// Export
export default EvaluationList;
